import React, { useState, useMemo } from "react";
import FilterBar from "../components/FilterBar";

// Mock Data
const COMPANIES = [
  { id: 1, name: "ПАО «Газпром»", industry: "Энергетика", vacancies: 5, city: "Омск" },
  { id: 2, name: "Сбербанк", industry: "Финтех", vacancies: 12, city: "Москва" },
  { id: 3, name: "Яндекс", industry: "IT", vacancies: 8, city: "Москва" },
  { id: 4, name: "Мостовик", industry: "Строительство", vacancies: 2, city: "Омск" },
  { id: 5, name: "Омский НПЗ", industry: "Энергетика", vacancies: 7, city: "Омск" },
  { id: 6, name: "Effective", industry: "IT", vacancies: 3, city: "Омск" },
];

const OrganizationsPage = () => {
  const [searchQuery, setSearchQuery] = useState("");
  const [filters, setFilters] = useState({ industry: "", city: "" });

  // Логика фильтрации
  const filteredCompanies = useMemo(() => {
    return COMPANIES.filter(company => {
      const matchesSearch = company.name.toLowerCase().includes(searchQuery.toLowerCase());
      const matchesIndustry = filters.industry ? company.industry === filters.industry : true;
      const matchesCity = filters.city ? company.city === filters.city : true;

      return matchesSearch && matchesIndustry && matchesCity;
    });
  }, [searchQuery, filters]);

  const handleFilterChange = (name, value) => {
    setFilters(prev => ({ ...prev, [name]: value }));
  };

  return (
    <div className="container page-content">
      <h2 style={{ marginBottom: "20px" }}>Организации-партнеры</h2>

      {/* Фильтры */}
      <FilterBar 
        onSearch={setSearchQuery}
        onFilterChange={handleFilterChange}
        filters={[
          { 
            name: "industry", 
            placeholder: "Все отрасли", 
            options: ["IT", "Энергетика", "Строительство", "Финтех"] 
          },
          { 
            name: "city", 
            placeholder: "Все города", 
            options: ["Омск", "Москва"] 
          }
        ]}
      />

      {/* Список */}
      <div style={{ display: "flex", flexDirection: "column", gap: "15px" }}>
        {filteredCompanies.map((company) => (
          <div key={company.id} className="practice-card" style={{ alignItems: "center" }}>
            <div style={{ display: "flex", alignItems: "center", gap: "15px" }}>
              {/* Логотип-заглушка */}
              <div style={{ 
                width: "50px", height: "50px", background: "#f1f5f9", borderRadius: "50%",
                display: "flex", alignItems: "center", justifyContent: "center",
                fontWeight: "bold", color: "#64748b"
              }}>
                {company.name[0]}
              </div>

              <div>
                <h3 style={{ fontSize: "18px", marginBottom: "4px" }}>{company.name}</h3>
                <p style={{ color: "#64748b", fontSize: "14px", margin: 0 }}>
                  {company.industry} • {company.city} • <strong>{company.vacancies} вакансий</strong>
                </p>
              </div>
            </div>
            <button className="btn-outline">Подробнее</button>
          </div>
        ))}
        
        {filteredCompanies.length === 0 && (
          <div style={{ textAlign: "center", color: "#94a3b8", padding: "20px" }}>Ничего не найдено</div>
        )}
      </div>
    </div>
  );
};

export default OrganizationsPage;
