import React from "react";

// –≠—Ç–æ—Ç –∫–æ–º–ø–æ–Ω–µ–Ω—Ç –ø—Ä–∏–Ω–∏–º–∞–µ—Ç –∑–∞–≥–æ–ª–æ–≤–æ–∫ (title) –∏ —Å–ø–∏—Å–æ–∫ –¥–∞–Ω–Ω—ã—Ö (data)
// –ß—Ç–æ–±—ã –º—ã –º–æ–≥–ª–∏ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å –µ–≥–æ –¥–ª—è —Ä–∞–∑–Ω—ã—Ö —Å—Ç—Ä–∞–Ω–∏—Ü
const DocumentsPage = ({ title, type }) => {
  
  // –ü—Ä–∏–º–µ—Ä –¥–∞–Ω–Ω—ã—Ö (–∑–∞–≥–ª—É—à–∫–∏)
  // –í —Ä–µ–∞–ª—å–Ω–æ–º –ø—Ä–æ–µ–∫—Ç–µ –æ–Ω–∏ –±—É–¥—É—Ç –ø—Ä–∏—Ö–æ–¥–∏—Ç—å —Å —Å–µ—Ä–≤–µ—Ä–∞
  const items = [
    { id: 1, title: "–ü—Ä–∏–º–µ—Ä –¥–æ–∫—É–º–µ–Ω—Ç–∞ 1", desc: "PDF, 2.4 MB ‚Ä¢ –ó–∞–≥—Ä—É–∂–µ–Ω–æ –≤—á–µ—Ä–∞" },
    { id: 2, title: "–ü—Ä–∏–º–µ—Ä –¥–æ–∫—É–º–µ–Ω—Ç–∞ 2", desc: "DOCX, 1.1 MB ‚Ä¢ –ó–∞–≥—Ä—É–∂–µ–Ω–æ –Ω–µ–¥–µ–ª—é –Ω–∞–∑–∞–¥" },
    { id: 3, title: "–ü—Ä–∏–º–µ—Ä –¥–æ–∫—É–º–µ–Ω—Ç–∞ 3", desc: "PDF, 5.0 MB ‚Ä¢ –í–∞–∂–Ω—ã–π —Ñ–∞–π–ª" },
  ];

  return (
    <div className="container page-content">
      <h2 style={{ marginBottom: "20px" }}>{title}</h2>

      <div className="documents-list" style={{ display: "flex", flexDirection: "column", gap: "15px" }}>
        {items.map((item) => (
          <div 
            key={item.id} 
            className="practice-card" // –ò—Å–ø–æ–ª—å–∑—É–µ–º —Ç–æ—Ç –∂–µ –∫–ª–∞—Å—Å —Å—Ç–∏–ª–µ–π, —á—Ç–æ –∏ –≤ –ü—Ä–∞–∫—Ç–∏–∫–∞—Ö
            style={{ alignItems: "center" }} // –ù–µ–º–Ω–æ–≥–æ –ø–æ–¥–ø—Ä–∞–≤–∏–º –≤—ã—Ä–∞–≤–Ω–∏–≤–∞–Ω–∏–µ
          >
            <div style={{ display: "flex", alignItems: "center", gap: "15px" }}>
              {/* –ò–∫–æ–Ω–∫–∞ —Ñ–∞–π–ª–∞ */}
              <div style={{ 
                width: "40px", 
                height: "40px", 
                background: "#eff6ff", 
                color: "#2563eb",
                borderRadius: "8px",
                display: "flex", 
                alignItems: "center", 
                justifyContent: "center",
                fontSize: "20px"
              }}>
                üìÑ
              </div>
              
              <div>
                <h3 style={{ fontSize: "16px", marginBottom: "4px" }}>{item.title}</h3>
                <p style={{ color: "#64748b", fontSize: "13px", margin: 0 }}>
                  {item.desc}
                </p>
              </div>
            </div>

            <button className="btn-outline">
              {type === "download" ? "–°–∫–∞—á–∞—Ç—å" : "–û—Ç–∫—Ä—ã—Ç—å"}
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default DocumentsPage;
