import React, { useState } from "react";
import { useAuth } from "../context/AuthContext";

const ProfilePage = () => {
  const { user } = useAuth();
  
  // –°–æ—Å—Ç–æ—è–Ω–∏–µ –∞–∫—Ç–∏–≤–Ω–æ–π –≤–∫–ª–∞–¥–∫–∏ (–Ω—É–∂–Ω–æ —Ç–æ–ª—å–∫–æ –¥–ª—è —Å—Ç—É–¥–µ–Ω—Ç–∞)
  const [activeTab, setActiveTab] = useState("applications"); // 'applications' | 'resume'

  // –î–∞–Ω–Ω—ã–µ –æ–± –æ—Ç–∫–ª–∏–∫–∞—Ö (–¥–ª—è –°—Ç—É–¥–µ–Ω—Ç–∞)
  const myApplications = [
    { id: 1, company: "–°–±–µ—Ä–±–∞–Ω–∫", position: "–°—Ç–∞–∂–µ—Ä Java", status: "–ù–∞ —Ä–∞—Å—Å–º–æ—Ç—Ä–µ–Ω–∏–∏", date: "24.11.2025" },
    { id: 2, company: "–ì–∞–∑–ø—Ä–æ–º", position: "–ê–Ω–∞–ª–∏—Ç–∏–∫", status: "–ü—Ä–∏–≥–ª–∞—à–µ–Ω–∏–µ", date: "20.11.2025" },
    { id: 3, company: "–ú–æ—Å—Ç–æ–≤–∏–∫", position: "–ò–Ω–∂–µ–Ω–µ—Ä", status: "–û—Ç–∫–∞–∑", date: "15.11.2025" },
  ];

  // –§—É–Ω–∫—Ü–∏—è –¥–ª—è —Ü–≤–µ—Ç–∞ —Å—Ç–∞—Ç—É—Å–∞ (–¥–ª—è –°—Ç—É–¥–µ–Ω—Ç–∞)
  const getStatusStyle = (status) => {
    switch (status) {
      case "–ü—Ä–∏–≥–ª–∞—à–µ–Ω–∏–µ": return { background: "#dcfce7", color: "#166534" };
      case "–û—Ç–∫–∞–∑": return { background: "#fee2e2", color: "#991b1b" };
      default: return { background: "#f1f5f9", color: "#475569" };
    }
  };

  if (!user) return <div className="container page-content">–ó–∞–≥—Ä—É–∑–∫–∞...</div>;

  // --- –í–°–ü–û–ú–û–ì–ê–¢–ï–õ–¨–ù–´–ô –ö–û–ú–ü–û–ù–ï–ù–¢ –®–ê–ü–ö–ò ---
  const ProfileHeader = ({ roleIcon, roleName, roleColor, borderColor, children }) => (
    <div style={{ 
      background: "white", 
      padding: "30px", 
      borderRadius: "16px", 
      boxShadow: "0 4px 6px -1px rgba(0, 0, 0, 0.1)",
      display: "flex", 
      justifyContent: "space-between", 
      alignItems: "center", 
      marginBottom: "30px",
      borderLeft: `6px solid ${borderColor}`
    }}>
      <div style={{ display: "flex", alignItems: "center", gap: "30px" }}>
        <div style={{ 
          width: "100px", height: "100px", borderRadius: "50%", 
          background: roleColor, 
          display: "flex", alignItems: "center", justifyContent: "center",
          fontSize: "40px", color: "white", fontWeight: "bold"
        }}>
          {roleIcon}
        </div>
        <div>
          <h1 style={{ fontSize: "24px", marginBottom: "8px", color: "#1e293b" }}>{user.name}</h1>
          <p style={{ color: "#64748b", marginBottom: "15px" }}>
            {roleName} ‚Ä¢ {user.email}
          </p>
          <div style={{ display: "flex", gap: "10px" }}>
             {/* –î–ª—è —Å—Ç—É–¥–µ–Ω—Ç–∞ –¥–æ–±–∞–≤–ª—è–µ–º –±–µ–π–¥–∂ —Å—Ç–∞—Ç—É—Å–∞ –≤ —à–∞–ø–∫—É */}
            {user.role === 'student' && (
               <span className="btn-pill" style={{ background: "#eff6ff", color: "#2563eb", cursor: "default", marginRight: "10px" }}>
                üîç –í –ø–æ–∏—Å–∫–µ –ø—Ä–∞–∫—Ç–∏–∫–∏
              </span>
            )}
            <button className="btn-outline" style={{ padding: "6px 15px", fontSize: "13px" }}>
              –†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å –ø—Ä–æ—Ñ–∏–ª—å
            </button>
            {/* –î–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω—ã–µ –∫–Ω–æ–ø–∫–∏ (–¥–ª—è –ö–æ–º–ø–∞–Ω–∏–∏) */}
            {children}
          </div>
        </div>
      </div>
    </div>
  );

  // --- 1. –ü–†–û–§–ò–õ–¨ –°–¢–£–î–ï–ù–¢–ê ---
  if (user.role === "student") {
    return (
      <div className="container page-content">
        <ProfileHeader 
          roleIcon={user.name[0]} 
          roleName="–°—Ç—É–¥–µ–Ω—Ç 4 –∫—É—Ä—Å–∞" 
          roleColor="linear-gradient(135deg, #2563eb 0%, #1e40af 100%)" // –°–∏–Ω–∏–π
          borderColor="#2563eb"
        />

        {/* –í–∫–ª–∞–¥–∫–∏ (–¢–æ—Ç —Å–∞–º—ã–π –∫—Ä–∞—Å–∏–≤—ã–π –¥–∏–∑–∞–π–Ω) */}
        <div style={{ display: "flex", gap: "20px", marginBottom: "20px", borderBottom: "1px solid #e2e8f0" }}>
          <button 
            onClick={() => setActiveTab("applications")}
            style={{ 
              padding: "10px 0", background: "none", border: "none", 
              borderBottom: activeTab === "applications" ? "2px solid #2563eb" : "2px solid transparent",
              color: activeTab === "applications" ? "#2563eb" : "#64748b", fontWeight: "bold", cursor: "pointer"
            }}
          >
            –ú–æ–∏ –æ—Ç–∫–ª–∏–∫–∏
          </button>
          <button 
            onClick={() => setActiveTab("resume")}
            style={{ 
              padding: "10px 0", background: "none", border: "none", 
              borderBottom: activeTab === "resume" ? "2px solid #2563eb" : "2px solid transparent",
              color: activeTab === "resume" ? "#2563eb" : "#64748b", fontWeight: "bold", cursor: "pointer"
            }}
          >
            –ú–æ–µ —Ä–µ–∑—é–º–µ
          </button>
        </div>

        {/* –ö–æ–Ω—Ç–µ–Ω—Ç –≤–∫–ª–∞–¥–æ–∫ */}
        {activeTab === "applications" ? (
          <div style={{ background: "white", borderRadius: "12px", overflow: "hidden", border: "1px solid #e2e8f0" }}>
            {myApplications.map((app, index) => (
              <div key={app.id} style={{ 
                padding: "20px", 
                display: "flex", 
                justifyContent: "space-between", 
                alignItems: "center",
                borderBottom: index !== myApplications.length - 1 ? "1px solid #f1f5f9" : "none"
              }}>
                <div>
                  <h3 style={{ fontSize: "16px", marginBottom: "4px" }}>{app.position}</h3>
                  <p style={{ color: "#64748b", fontSize: "14px", margin: 0 }}>{app.company} ‚Ä¢ –û—Ç–ø—Ä–∞–≤–ª–µ–Ω–æ {app.date}</p>
                </div>
                <span style={{ 
                  padding: "6px 12px", borderRadius: "20px", fontSize: "13px", fontWeight: "600",
                  ...getStatusStyle(app.status)
                }}>
                  {app.status}
                </span>
              </div>
            ))}
          </div>
        ) : (
          <div style={{ padding: "40px", textAlign: "center", background: "white", borderRadius: "12px", border: "1px solid #e2e8f0" }}>
            <div style={{ fontSize: "40px", marginBottom: "10px" }}>üìÑ</div>
            <h3 style={{ marginBottom: "10px" }}>–í–∞—à–µ —Ä–µ–∑—é–º–µ –∑–∞–≥—Ä—É–∂–µ–Ω–æ</h3>
            <p style={{ color: "#64748b", marginBottom: "20px" }}>–§–∞–π–ª: Ivanov_Resume.pdf (2.4 MB)</p>
            <button className="btn-primary" style={{ width: "auto" }}>–°–∫–∞—á–∞—Ç—å —Ä–µ–∑—é–º–µ</button>
          </div>
        )}
      </div>
    );
  }

  // --- 2. –ü–†–û–§–ò–õ–¨ –ö–û–ú–ü–ê–ù–ò–ò ---
  if (user.role === "company") {
    return (
      <div className="container page-content">
        <ProfileHeader 
          roleIcon="üè¢" 
          roleName="–†–∞–±–æ—Ç–æ–¥–∞—Ç–µ–ª—å" 
          roleColor="linear-gradient(135deg, #1e3a8a 0%, #172554 100%)" // –¢–µ–º–Ω–æ-—Å–∏–Ω–∏–π
          borderColor="#1e3a8a"
        >
           <button className="btn-primary" style={{ padding: "6px 15px", fontSize: "13px", width: "auto" }}>
            + –î–æ–±–∞–≤–∏—Ç—å –≤–∞–∫–∞–Ω—Å–∏—é
          </button>
        </ProfileHeader>

        <h2 style={{ marginBottom: "20px" }}>–ê–∫—Ç–∏–≤–Ω—ã–µ –≤–∞–∫–∞–Ω—Å–∏–∏</h2>
        <div style={{display: "grid", gap: "15px"}}>
          {/* –ö–∞—Ä—Ç–æ—á–∫–∞ –≤–∞–∫–∞–Ω—Å–∏–∏ –≤ —Å—Ç–∏–ª–µ "–æ—Ç–∫–ª–∏–∫–æ–≤", —á—Ç–æ–±—ã –±—ã–ª–æ –∫—Ä–∞—Å–∏–≤–æ */}
          <div style={{ background: "white", padding: "20px", borderRadius: "12px", border: "1px solid #e2e8f0" }}>
            <div style={{display: "flex", justifyContent: "space-between", marginBottom: "10px"}}>
               <h3 style={{fontSize: "18px"}}>–ò–Ω–∂–µ–Ω–µ—Ä-–∫–æ–Ω—Å—Ç—Ä—É–∫—Ç–æ—Ä (Junior)</h3>
               <span style={{color: "#2563eb", fontWeight: "bold", background: "#eff6ff", padding: "4px 10px", borderRadius: "8px"}}>3 –Ω–æ–≤—ã—Ö –æ—Ç–∫–ª–∏–∫–∞</span>
            </div>
            <p style={{color: "#64748b", fontSize: "14px"}}>–û–ø—É–±–ª–∏–∫–æ–≤–∞–Ω–æ: 20.11.2025 ‚Ä¢ 40 000 ‚ÇΩ</p>
          </div>

          <div style={{ background: "white", padding: "20px", borderRadius: "12px", border: "1px solid #e2e8f0" }}>
            <div style={{display: "flex", justifyContent: "space-between", marginBottom: "10px"}}>
               <h3 style={{fontSize: "18px"}}>–ë—É—Ö–≥–∞–ª—Ç–µ—Ä (–°—Ç–∞–∂–∏—Ä–æ–≤–∫–∞)</h3>
               <span style={{color: "#64748b", background: "#f1f5f9", padding: "4px 10px", borderRadius: "8px"}}>–ù–µ—Ç –Ω–æ–≤—ã—Ö –æ—Ç–∫–ª–∏–∫–æ–≤</span>
            </div>
            <p style={{color: "#64748b", fontSize: "14px"}}>–û–ø—É–±–ª–∏–∫–æ–≤–∞–Ω–æ: 15.11.2025 ‚Ä¢ –ü–æ –¥–æ–≥–æ–≤–æ—Ä–µ–Ω–Ω–æ—Å—Ç–∏</p>
          </div>
        </div>
      </div>
    );
  }

  // --- 3. –ü–†–û–§–ò–õ–¨ –°–û–¢–†–£–î–ù–ò–ö–ê ---
  if (user.role === "employee") {
    return (
      <div className="container page-content">
        <ProfileHeader 
          roleIcon="üë©‚Äçüè´" 
          roleName="–°–æ—Ç—Ä—É–¥–Ω–∏–∫ –í–£–ó–∞" 
          roleColor="linear-gradient(135deg, #0ea5e9 0%, #0284c7 100%)" // –ì–æ–ª—É–±–æ–π
          borderColor="#0ea5e9"
        />

        <h2>–ü–∞–Ω–µ–ª—å —É–ø—Ä–∞–≤–ª–µ–Ω–∏—è</h2>
        <div style={{display: "grid", gridTemplateColumns: "repeat(3, 1fr)", gap: "20px", marginTop: "20px"}}>
          
          <div style={{ background: "white", padding: "30px", borderRadius: "12px", textAlign: "center", border: "1px solid #e2e8f0", cursor: "pointer" }}>
            <div style={{fontSize: "30px", marginBottom: "10px"}}>üìÑ</div>
            <h3>–ù–æ–≤—ã–µ –¥–æ–∫—É–º–µ–Ω—Ç—ã</h3>
            <p style={{fontSize: "32px", fontWeight: "bold", color: "#0ea5e9", margin: "10px 0"}}>12</p>
            <p style={{fontSize: "13px", color: "#64748b"}}>–¢—Ä–µ–±—É—é—Ç –ø—Ä–æ–≤–µ—Ä–∫–∏</p>
          </div>

          <div style={{ background: "white", padding: "30px", borderRadius: "12px", textAlign: "center", border: "1px solid #e2e8f0", cursor: "pointer" }}>
            <div style={{fontSize: "30px", marginBottom: "10px"}}>‚úçÔ∏è</div>
            <h3>–î–æ–≥–æ–≤–æ—Ä—ã</h3>
            <p style={{fontSize: "32px", fontWeight: "bold", color: "#16a34a", margin: "10px 0"}}>5</p>
            <p style={{fontSize: "13px", color: "#64748b"}}>–ù–∞ –ø–æ–¥–ø–∏—Å–∞–Ω–∏–∏</p>
          </div>

          <div style={{ background: "white", padding: "30px", borderRadius: "12px", textAlign: "center", border: "1px solid #e2e8f0", cursor: "pointer" }}>
            <div style={{fontSize: "30px", marginBottom: "10px"}}>üë•</div>
            <h3>–°—Ç—É–¥–µ–Ω—Ç—ã</h3>
            <p style={{fontSize: "32px", fontWeight: "bold", color: "#475569", margin: "10px 0"}}>142</p>
            <p style={{fontSize: "13px", color: "#64748b"}}>–ù–∞ –ø—Ä–∞–∫—Ç–∏–∫–µ</p>
          </div>

        </div>
      </div>
    );
  }

  return <div>–†–æ–ª—å –Ω–µ –æ–ø—Ä–µ–¥–µ–ª–µ–Ω–∞</div>;
};

export default ProfilePage;
