          <div style={{ display: "flex", gap: "10px" }}>
            <button className="btn-outline" style={{ padding: "6px 15px", fontSize: "13px" }}>
              –†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å –ø—Ä–æ—Ñ–∏–ª—å
            </button>
            {/* –°—é–¥–∞ –≤—Å—Ç–∞–≤—è—Ç—Å—è –¥–æ–ø. –∫–Ω–æ–ø–∫–∏, –µ—Å–ª–∏ –æ–Ω–∏ –µ—Å—Ç—å */}
            {children}
          </div>
        </div>
      </div>
    </div>
  );

  // --- 1. –ü–†–û–§–ò–õ–¨ –°–¢–£–î–ï–ù–¢–ê ---
  if (user.role === "student") {
    return (
      <div className="container page-content">
        <ProfileHeader 
          roleIcon="üéì" 
          roleName="–°—Ç—É–¥–µ–Ω—Ç" 
          roleColor="linear-gradient(135deg, #2563eb 0%, #1e40af 100%)" // –°–∏–Ω–∏–π
          borderColor="#2563eb"
        />

        {/* –í–∫–ª–∞–¥–∫–∏ —Å—Ç—É–¥–µ–Ω—Ç–∞ (–í–µ—Ä–Ω—É–ª–∏ –ª–æ–≥–∏–∫—É!) */}
        <div style={{ display: "flex", gap: "20px", marginBottom: "20px", borderBottom: "1px solid #e2e8f0" }}>
          <button 
            onClick={() => setActiveTab("applications")}
            style={{ 
              padding: "10px 0", background: "none", border: "none", 
              borderBottom: activeTab === "applications" ? "2px solid #2563eb" : "2px solid transparent",
              color: activeTab === "applications" ? "#2563eb" : "#64748b", fontWeight: "bold", cursor: "pointer"
            }}
          >
            –ú–æ–∏ –æ—Ç–∫–ª–∏–∫–∏
          </button>
          <button 
            onClick={() => setActiveTab("resume")}
            style={{ 
              padding: "10px 0", background: "none", border: "none", 
              borderBottom: activeTab === "resume" ? "2px solid #2563eb" : "2px solid transparent",
              color: activeTab === "resume" ? "#2563eb" : "#64748b", fontWeight: "bold", cursor: "pointer"
            }}
          >
            –ú–æ–µ —Ä–µ–∑—é–º–µ
          </button>
        </div>

        {/* –ö–æ–Ω—Ç–µ–Ω—Ç –≤–∫–ª–∞–¥–æ–∫ */}
        {activeTab === "applications" ? (
          <div className="card">
            <div style={{display: "flex", justifyContent: "space-between", alignItems: "center"}}>
              <div>
                <h3>–°—Ç–∞–∂–µ—Ä Java-—Ä–∞–∑—Ä–∞–±–æ—Ç—á–∏–∫</h3>
                <p style={{color: "#64748b"}}>–ü–ê–û –°–±–µ—Ä–±–∞–Ω–∫ ‚Ä¢ –û—Ç–ø—Ä–∞–≤–ª–µ–Ω–æ –≤—á–µ—Ä–∞</p>
              </div>
              <span style={{background: "#fef3c7", color: "#d97706", padding: "5px 10px", borderRadius: "15px", fontSize: "13px", fontWeight: "bold"}}>–ù–∞ —Ä–∞—Å—Å–º–æ—Ç—Ä–µ–Ω–∏–∏</span>
            </div>
          </div>
        ) : (
          // –í–∫–ª–∞–¥–∫–∞ –†–µ–∑—é–º–µ
          <div style={{ padding: "40px", textAlign: "center", background: "white", borderRadius: "12px", border: "1px solid #e2e8f0" }}>
            <div style={{ fontSize: "40px", marginBottom: "10px" }}>üìÑ</div>
            <h3 style={{ marginBottom: "10px" }}>–í–∞—à–µ —Ä–µ–∑—é–º–µ –∑–∞–≥—Ä—É–∂–µ–Ω–æ</h3>
            <p style={{ color: "#64748b", marginBottom: "20px" }}>–§–∞–π–ª: Ivanov_Resume.pdf (2.4 MB)</p>
            <button className="btn-primary" style={{ width: "auto" }}>–°–∫–∞—á–∞—Ç—å —Ä–µ–∑—é–º–µ</button>
            <button className="btn-outline" style={{ width: "auto", marginLeft: "10px" }}>–ó–∞–≥—Ä—É–∑–∏—Ç—å –Ω–æ–≤–æ–µ</button>
          </div>
        )}
      </div>
    );
  }

  // --- 2. –ü–†–û–§–ò–õ–¨ –ö–û–ú–ü–ê–ù–ò–ò ---
  if (user.role === "company") {
    return (
      <div className="container page-content">
        <ProfileHeader 
          roleIcon="üè¢" 
          roleName="–†–∞–±–æ—Ç–æ–¥–∞—Ç–µ–ª—å" 
          roleColor="linear-gradient(135deg, #1e3a8a 0%, #172554 100%)" // –¢–µ–º–Ω–æ-—Å–∏–Ω–∏–π (–∫–∞–∫ –±—Ä–µ–Ω–¥)
          borderColor="#1e3a8a"
        >
          {/* –ö–Ω–æ–ø–∫–∞ –ø–µ—Ä–µ–µ—Ö–∞–ª–∞ —Å—é–¥–∞ */}
          <button className="btn-primary" style={{ padding: "6px 15px", fontSize: "13px", width: "auto" }}>
            + –î–æ–±–∞–≤–∏—Ç—å –≤–∞–∫–∞–Ω—Å–∏—é
          </button>
        </ProfileHeader>

        <h2 style={{ marginBottom: "20px" }}>–ê–∫—Ç–∏–≤–Ω—ã–µ –≤–∞–∫–∞–Ω—Å–∏–∏</h2>
        <div style={{display: "grid", gap: "15px"}}>
          <div className="card">
            <div style={{display: "flex", justifyContent: "space-between"}}>
               <h3>–ò–Ω–∂–µ–Ω–µ—Ä-–∫–æ–Ω—Å—Ç—Ä—É–∫—Ç–æ—Ä (Junior)</h3>
               <span style={{color: "#2563eb", fontWeight: "bold"}}>3 –Ω–æ–≤—ã—Ö –æ—Ç–∫–ª–∏–∫–∞</span>
            </div>
            <p style={{color: "#64748b", marginTop: "5px"}}>–û–ø—É–±–ª–∏–∫–æ–≤–∞–Ω–æ: 20.11.2025</p>
          </div>

          <div className="card">
            <div style={{display: "flex", justifyContent: "space-between"}}>
               <h3>–ë—É—Ö–≥–∞–ª—Ç–µ—Ä (–°—Ç–∞–∂–∏—Ä–æ–≤–∫–∞)</h3>
               <span style={{color: "#64748b"}}>–ù–µ—Ç –Ω–æ–≤—ã—Ö –æ—Ç–∫–ª–∏–∫–æ–≤</span>
            </div>
            <p style={{color: "#64748b", marginTop: "5px"}}>–û–ø—É–±–ª–∏–∫–æ–≤–∞–Ω–æ: 15.11.2025</p>
