  // --- 3. –ü–†–û–§–ò–õ–¨ –°–û–¢–†–£–î–ù–ò–ö–ê ---
  if (user.role === "employee") {
    return (
      <div className="container page-content">
        <ProfileHeader 
          roleIcon="üë©‚Äçüè´" 
