import React, { useState, useMemo } from "react";
import FilterBar from "../components/FilterBar";

// 1. –°–æ–∑–¥–∞–µ–º –±–∞–∑—É –¥–∞–Ω–Ω—ã—Ö –ø—Ä–∞–∫—Ç–∏–∫ (Mock Data)
const MOCK_PRACTICES = [
  { id: 1, title: "–°—Ç–∞–∂–µ—Ä Java-—Ä–∞–∑—Ä–∞–±–æ—Ç—á–∏–∫", company: "–°–±–µ—Ä–±–∞–Ω–∫", city: "–û–º—Å–∫", type: "–û–ø–ª–∞—á–∏–≤–∞–µ–º–∞—è" },
  { id: 2, title: "–ò–Ω–∂–µ–Ω–µ—Ä-–ø—Ä–æ–µ–∫—Ç–∏—Ä–æ–≤—â–∏–∫", company: "–ú–æ—Å—Ç–æ–≤–∏–∫", city: "–û–º—Å–∫", type: "–ù–µ–æ–ø–ª–∞—á–∏–≤–∞–µ–º–∞—è" },
  { id: 3, title: "–ú–µ–Ω–µ–¥–∂–µ—Ä –ø—Ä–æ–µ–∫—Ç–æ–≤", company: "–ì–∞–∑–ø—Ä–æ–º –ù–µ—Ñ—Ç—å", city: "–û–º—Å–∫", type: "–û–ø–ª–∞—á–∏–≤–∞–µ–º–∞—è" },
  { id: 4, title: "Frontend-—Ä–∞–∑—Ä–∞–±–æ—Ç—á–∏–∫", company: "Effective", city: "–£–¥–∞–ª–µ–Ω–Ω–æ", type: "–û–ø–ª–∞—á–∏–≤–∞–µ–º–∞—è" },
  { id: 5, title: "–ü–æ–º–æ—â–Ω–∏–∫ –±—É—Ö–≥–∞–ª—Ç–µ—Ä–∞", company: "–ú–∞–≥–Ω–∏—Ç", city: "–û–º—Å–∫", type: "–ù–µ–æ–ø–ª–∞—á–∏–≤–∞–µ–º–∞—è" },
  { id: 6, title: "–ê–Ω–∞–ª–∏—Ç–∏–∫ –¥–∞–Ω–Ω—ã—Ö", company: "–°–±–µ—Ä–±–∞–Ω–∫", city: "–ú–æ—Å–∫–≤–∞", type: "–û–ø–ª–∞—á–∏–≤–∞–µ–º–∞—è" },
];

const PracticesPage = () => {
  // 2. –°–æ—Å—Ç–æ—è–Ω–∏—è –¥–ª—è –ø–æ–∏—Å–∫–∞ –∏ —Ñ–∏–ª—å—Ç—Ä–æ–≤
  const [searchQuery, setSearchQuery] = useState("");
  const [filters, setFilters] = useState({
    city: "",
    type: ""
  });

  // 3. –õ–æ–≥–∏–∫–∞ —Ñ–∏–ª—å—Ç—Ä–∞—Ü–∏–∏ (—Å—Ä–∞–±–∞—Ç—ã–≤–∞–µ—Ç –∞–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏ –ø—Ä–∏ –∏–∑–º–µ–Ω–µ–Ω–∏–∏ –ø–æ–∏—Å–∫–∞ –∏–ª–∏ —Ñ–∏–ª—å—Ç—Ä–æ–≤)
  const filteredPractices = useMemo(() => {
    return MOCK_PRACTICES.filter(item => {
      // –ü—Ä–æ–≤–µ—Ä–∫–∞ –ø–æ –ø–æ–∏—Å–∫—É (–∏—â–µ–º –≤ –Ω–∞–∑–≤–∞–Ω–∏–∏ –∏ –∫–æ–º–ø–∞–Ω–∏–∏)
      const matchesSearch = 
        item.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
        item.company.toLowerCase().includes(searchQuery.toLowerCase());

      // –ü—Ä–æ–≤–µ—Ä–∫–∞ –ø–æ –≥–æ—Ä–æ–¥—É
      const matchesCity = filters.city ? item.city === filters.city : true;
      
      // –ü—Ä–æ–≤–µ—Ä–∫–∞ –ø–æ —Ç–∏–ø—É
      const matchesType = filters.type ? item.type === filters.type : true;

      return matchesSearch && matchesCity && matchesType;
    });
  }, [searchQuery, filters]);

  // –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –∏–∑–º–µ–Ω–µ–Ω–∏—è —Ñ–∏–ª—å—Ç—Ä–æ–≤
  const handleFilterChange = (name, value) => {
    setFilters(prev => ({ ...prev, [name]: value }));
  };

  return (
    <div className="container page-content">
      <h2 style={{ marginBottom: "20px" }}>–°–ø–∏—Å–æ–∫ –ø—Ä–∞–∫—Ç–∏–∫</h2>

      {/* 4. –í—Å—Ç–∞–≤–ª—è–µ–º –Ω–∞—à –Ω–æ–≤—ã–π –∫–æ–º–ø–æ–Ω–µ–Ω—Ç —Ñ–∏–ª—å—Ç—Ä–æ–≤ */}
      <FilterBar 
        onSearch={setSearchQuery}
        onFilterChange={handleFilterChange}
        filters={[
          { 
            name: "city", 
            placeholder: "–í—Å–µ –≥–æ—Ä–æ–¥–∞", 
            options: ["–û–º—Å–∫", "–ú–æ—Å–∫–≤–∞", "–£–¥–∞–ª–µ–Ω–Ω–æ"] 
          },
          { 
            name: "type", 
            placeholder: "–¢–∏–ø –ø—Ä–∞–∫—Ç–∏–∫–∏", 
            options: ["–û–ø–ª–∞—á–∏–≤–∞–µ–º–∞—è", "–ù–µ–æ–ø–ª–∞—á–∏–≤–∞–µ–º–∞—è"] 
          }
        ]}
      />

      {/* 5. –í—ã–≤–æ–¥–∏–º –æ—Ç—Ñ–∏–ª—å—Ç—Ä–æ–≤–∞–Ω–Ω—ã–π —Å–ø–∏—Å–æ–∫ */}
      <div className="practices-list" style={{ display: "flex", flexDirection: "column", gap: "15px" }}>
        
        {filteredPractices.length > 0 ? (
          filteredPractices.map((practice) => (
            <div key={practice.id} className="practice-card">
              <div>
                <h3 style={{ fontSize: "18px", marginBottom: "5px" }}>{practice.title}</h3>
                <p style={{ color: "#64748b", margin: 0 }}>
                  <strong>{practice.company}</strong> ‚Ä¢ {practice.city}
                </p>
                <span style={{ 
                  display: "inline-block", 
                  background: practice.type === "–û–ø–ª–∞—á–∏–≤–∞–µ–º–∞—è" ? "#dcfce7" : "#f1f5f9",
                  color: practice.type === "–û–ø–ª–∞—á–∏–≤–∞–µ–º–∞—è" ? "#166534" : "#64748b",
                  fontSize: "12px",
                  padding: "4px 8px",
                  borderRadius: "4px",
                  marginTop: "8px"
                }}>
                  {practice.type}
                </span>
              </div>
              <button className="btn-outline">–ü–æ–¥–∞—Ç—å –∑–∞—è–≤–∫—É</button>
            </div>
          ))
        ) : (
          // –ï—Å–ª–∏ –Ω–∏—á–µ–≥–æ –Ω–µ –Ω–∞–π–¥–µ–Ω–æ
          <div style={{ textAlign: "center", padding: "40px", color: "#94a3b8" }}>
            –ü–æ –≤–∞—à–µ–º—É –∑–∞–ø—Ä–æ—Å—É –Ω–∏—á–µ–≥–æ –Ω–µ –Ω–∞–π–¥–µ–Ω–æ üòî
          </div>
        )}

      </div>
    </div>
  );
};

export default PracticesPage;
