import React, { useState } from "react";
import { useAuth } from "../context/AuthContext";

const ProfilePage = () => {
  const { user } = useAuth();
  const [activeTab, setActiveTab] = useState("tab1");

  if (!user) return <div className="container page-content">–ó–∞–≥—Ä—É–∑–∫–∞...</div>;

  // --- –í–°–ü–û–ú–û–ì–ê–¢–ï–õ–¨–ù–´–ï –ö–û–ú–ü–û–ù–ï–ù–¢–´ ---

  // –ö–∞—Ä—Ç–æ—á–∫–∞ —à–∞–ø–∫–∏ –ø—Ä–æ—Ñ–∏–ª—è (–æ–±—â–∞—è –¥–ª—è –≤—Å–µ—Ö, –Ω–æ —Å —Ä–∞–∑–Ω—ã–º–∏ —Ü–≤–µ—Ç–∞–º–∏)
  const ProfileHeader = ({ roleIcon, roleName, roleColor, borderColor }) => (
    <div style={{ 
      background: "white", 
      padding: "30px", 
      borderRadius: "16px", 
      boxShadow: "0 4px 6px -1px rgba(0, 0, 0, 0.1)",
      display: "flex", alignItems: "center", gap: "30px", marginBottom: "30px",
      borderLeft: `6px solid ${borderColor}` // –¶–≤–µ—Ç–Ω–∞—è –ø–æ–ª–æ—Å–∫–∞ —Å–ª–µ–≤–∞
    }}>
      <div style={{ 
        width: "100px", height: "100px", borderRadius: "50%", 
        background: roleColor, // –§–æ–Ω –∞–≤–∞—Ç–∞—Ä–∫–∏
        display: "flex", alignItems: "center", justifyContent: "center",
        fontSize: "40px", color: "white"
      }}>
        {roleIcon}
      </div>
      <div>
        <h1 style={{ fontSize: "24px", marginBottom: "8px", color: "#1e293b" }}>{user.name}</h1>
        <p style={{ color: "#64748b", marginBottom: "15px" }}>
