import React from "react";

const OrganizationsPage = () => {
  // Заглушки компаний
  const companies = [
    { id: 1, name: "ПАО «Газпром»", industry: "Энергетика", vacancies: 5 },
    { id: 2, name: "Сбербанк", industry: "Финтех / Банкинг", vacancies: 12 },
    { id: 3, name: "Яндекс", industry: "IT / Технологии", vacancies: 8 },
    { id: 4, name: "Мостовик", industry: "Строительство", vacancies: 2 },
  ];

  return (
    <div className="container page-content">
      <h2 style={{ marginBottom: "20px" }}>Организации-партнеры</h2>

      <div style={{ display: "flex", flexDirection: "column", gap: "15px" }}>
        {companies.map((company) => (
          <div key={company.id} className="practice-card" style={{ alignItems: "center" }}>
            <div style={{ display: "flex", alignItems: "center", gap: "15px" }}>
              {/* Логотип компании (заглушка) */}
              <div style={{ 
                width: "50px", 
                height: "50px", 
                background: "#f1f5f9", 
                borderRadius: "50%",
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                fontWeight: "bold",
                color: "#64748b"
              }}>
                {company.name[0]}
              </div>

              <div>
                <h3 style={{ fontSize: "18px", marginBottom: "4px" }}>{company.name}</h3>
                <p style={{ color: "#64748b", fontSize: "14px", margin: 0 }}>
                  {company.industry} • <strong>{company.vacancies} вакансий</strong>
                </p>
              </div>
            </div>

            <button className="btn-outline">Подробнее</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default OrganizationsPage;
