import React, { useState, useMemo } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import FilterBar from "../components/FilterBar";

// –ë–∞–∑–∞ –¥–∞–Ω–Ω—ã—Ö –ø—Ä–∞–∫—Ç–∏–∫ (Mock Data)
const MOCK_PRACTICES = [
  { id: 1, title: "–°—Ç–∞–∂–µ—Ä Java-—Ä–∞–∑—Ä–∞–±–æ—Ç—á–∏–∫", company: "–°–±–µ—Ä–±–∞–Ω–∫", city: "–û–º—Å–∫", type: "–û–ø–ª–∞—á–∏–≤–∞–µ–º–∞—è" },
  { id: 2, title: "–ò–Ω–∂–µ–Ω–µ—Ä-–ø—Ä–æ–µ–∫—Ç–∏—Ä–æ–≤—â–∏–∫", company: "–ú–æ—Å—Ç–æ–≤–∏–∫", city: "–û–º—Å–∫", type: "–ù–µ–æ–ø–ª–∞—á–∏–≤–∞–µ–º–∞—è" },
  { id: 3, title: "–ú–µ–Ω–µ–¥–∂–µ—Ä –ø—Ä–æ–µ–∫—Ç–æ–≤", company: "–ì–∞–∑–ø—Ä–æ–º –ù–µ—Ñ—Ç—å", city: "–û–º—Å–∫", type: "–û–ø–ª–∞—á–∏–≤–∞–µ–º–∞—è" },
  { id: 4, title: "Frontend-—Ä–∞–∑—Ä–∞–±–æ—Ç—á–∏–∫", company: "Effective", city: "–£–¥–∞–ª–µ–Ω–Ω–æ", type: "–û–ø–ª–∞—á–∏–≤–∞–µ–º–∞—è" },
  { id: 5, title: "–ü–æ–º–æ—â–Ω–∏–∫ –±—É—Ö–≥–∞–ª—Ç–µ—Ä–∞", company: "–ú–∞–≥–Ω–∏—Ç", city: "–û–º—Å–∫", type: "–ù–µ–æ–ø–ª–∞—á–∏–≤–∞–µ–º–∞—è" },
  { id: 6, title: "–ê–Ω–∞–ª–∏—Ç–∏–∫ –¥–∞–Ω–Ω—ã—Ö", company: "–°–±–µ—Ä–±–∞–Ω–∫", city: "–ú–æ—Å–∫–≤–∞", type: "–û–ø–ª–∞—á–∏–≤–∞–µ–º–∞—è" },
];

const PracticesPage = () => {
  const navigate = useNavigate();
  // –ü–æ–ª—É—á–∞–µ–º –¥–∞–Ω–Ω—ã–µ –∏–∑ –Ω–∞—à–µ–≥–æ –∫–æ–Ω—Ç–µ–∫—Å—Ç–∞ (—é–∑–µ—Ä –∏ —Å–ø–∏—Å–æ–∫ –µ–≥–æ –∑–∞—è–≤–æ–∫)
  const { user, userApplications, applyForPractice } = useAuth();

  const [searchQuery, setSearchQuery] = useState("");
  const [filters, setFilters] = useState({ city: "", type: "" });

  // –õ–æ–≥–∏–∫–∞ —Ñ–∏–ª—å—Ç—Ä–∞—Ü–∏–∏ —Å–ø–∏—Å–∫–∞
  const filteredPractices = useMemo(() => {
    return MOCK_PRACTICES.filter(item => {
      const matchesSearch = 
        item.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
        item.company.toLowerCase().includes(searchQuery.toLowerCase());

      const matchesCity = filters.city ? item.city === filters.city : true;
      const matchesType = filters.type ? item.type === filters.type : true;

      return matchesSearch && matchesCity && matchesType;
    });
  }, [searchQuery, filters]);

  const handleFilterChange = (name, value) => {
    setFilters(prev => ({ ...prev, [name]: value }));
  };

  // –ü—Ä–æ–≤–µ—Ä—è–µ–º, –ø–æ–¥–∞–≤–∞–ª –ª–∏ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å —É–∂–µ –∑–∞—è–≤–∫—É –Ω–∞ —ç—Ç—É –≤–∞–∫–∞–Ω—Å–∏—é
  const isApplied = (practiceTitle) => {
    // –ï—Å–ª–∏ —é–∑–µ—Ä –Ω–µ –≤–æ—à–µ–ª –∏–ª–∏ —É –Ω–µ–≥–æ –Ω–µ—Ç –∑–∞—è–≤–æ–∫ - false
    if (!user || !userApplications) return false;
    return userApplications.some(app => app.position === practiceTitle);
  };

  // –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –Ω–∞–∂–∞—Ç–∏—è –Ω–∞ –∫–Ω–æ–ø–∫—É
  const handleApply = (practice) => {
    if (!user) {
      // –ï—Å–ª–∏ –Ω–µ –∞–≤—Ç–æ—Ä–∏–∑–æ–≤–∞–Ω -> –æ—Ç–ø—Ä–∞–≤–ª—è–µ–º –Ω–∞ –≤—Ö–æ–¥
      const confirmLogin = window.confirm("–î–ª—è –ø–æ–¥–∞—á–∏ –∑–∞—è–≤–∫–∏ –Ω–µ–æ–±—Ö–æ–¥–∏–º–æ –≤–æ–π—Ç–∏ –≤ —Å–∏—Å—Ç–µ–º—É. –ü–µ—Ä–µ–µ—Ç–∏ –Ω–∞ —Å—Ç—Ä–∞–Ω–∏—Ü—É –≤—Ö–æ–¥–∞?");
      if (confirmLogin) {
        navigate("/login");
      }
      return;
    }

    // –ï—Å–ª–∏ –∞–≤—Ç–æ—Ä–∏–∑–æ–≤–∞–Ω -> –ø–æ–¥–∞–µ–º –∑–∞—è–≤–∫—É
    applyForPractice(practice);
    alert(`–ó–∞—è–≤–∫–∞ –Ω–∞ –≤–∞–∫–∞–Ω—Å–∏—é "${practice.title}" —É—Å–ø–µ—à–Ω–æ –æ—Ç–ø—Ä–∞–≤–ª–µ–Ω–∞!`);
  };

  return (
    <div className="container page-content">
      <h2 style={{ marginBottom: "20px" }}>–°–ø–∏—Å–æ–∫ –ø—Ä–∞–∫—Ç–∏–∫</h2>

      {/* –ü–∞–Ω–µ–ª—å —Ñ–∏–ª—å—Ç—Ä–æ–≤ */}
      <FilterBar 
        onSearch={setSearchQuery}
        onFilterChange={handleFilterChange}
        filters={[
          { 
            name: "city", 
            placeholder: "–í—Å–µ –≥–æ—Ä–æ–¥–∞", 
            options: ["–û–º—Å–∫", "–ú–æ—Å–∫–≤–∞", "–£–¥–∞–ª–µ–Ω–Ω–æ"] 
          },
          { 
            name: "type", 
            placeholder: "–¢–∏–ø –ø—Ä–∞–∫—Ç–∏–∫–∏", 
            options: ["–û–ø–ª–∞—á–∏–≤–∞–µ–º–∞—è", "–ù–µ–æ–ø–ª–∞—á–∏–≤–∞–µ–º–∞—è"] 
          }
        ]}
      />

      {/* –°–ø–∏—Å–æ–∫ –∫–∞—Ä—Ç–æ—á–µ–∫ */}
      <div className="practices-list" style={{ display: "flex", flexDirection: "column", gap: "15px" }}>
        
        {filteredPractices.length > 0 ? (
          filteredPractices.map((practice) => {
            const applied = isApplied(practice.title); // –°—Ç–∞—Ç—É—Å –∑–∞—è–≤–∫–∏

            return (
              <div key={practice.id} className="practice-card">
                <div>
                  <h3 style={{ fontSize: "18px", marginBottom: "5px" }}>{practice.title}</h3>
                  <p style={{ color: "#64748b", margin: 0 }}>
                    <strong>{practice.company}</strong> ‚Ä¢ {practice.city}
                  </p>
                  <span style={{ 
                    display: "inline-block", 
                    background: practice.type === "–û–ø–ª–∞—á–∏–≤–∞–µ–º–∞—è" ? "#dcfce7" : "#f1f5f9",
                    color: practice.type === "–û–ø–ª–∞—á–∏–≤–∞–µ–º–∞—è" ? "#166534" : "#64748b",
                    fontSize: "12px",
                    padding: "4px 8px",
                    borderRadius: "4px",
                    marginTop: "8px"
                  }}>
                    {practice.type}
                  </span>
                </div>
                
                {/* –ö–ù–û–ü–ö–ê –ó–ê–Ø–í–ö–ò */}
                <button 
                  className={applied ? "btn-outline" : "btn-primary"} // –†–∞–∑–Ω—ã–µ —Å—Ç–∏–ª–∏
                  onClick={() => !applied && handleApply(practice)}   // –ë–ª–æ–∫–∏—Ä—É–µ–º –∫–ª–∏–∫, –µ—Å–ª–∏ —É–∂–µ –ø–æ–¥–∞–Ω–æ
                  disabled={applied} // –î–µ–ª–∞–µ–º –∫–Ω–æ–ø–∫—É –Ω–µ–∞–∫—Ç–∏–≤–Ω–æ–π
                  style={{ 
                    minWidth: "140px",
                    opacity: applied ? 0.7 : 1,
                    cursor: applied ? "default" : "pointer",
                    // –ï—Å–ª–∏ –ø–æ–¥–∞–Ω–æ - —Å–µ—Ä—ã–π —Ü–≤–µ—Ç, –∏–Ω–∞—á–µ - —Å–∏–Ω–∏–π (–ø–æ —É–º–æ–ª—á–∞–Ω–∏—é btn-primary)
                    background: applied ? "#f1f5f9" : undefined,
                    color: applied ? "#64748b" : undefined,
                    borderColor: applied ? "#cbd5e1" : undefined
                  }}
                >
                  {applied ? "‚úÖ –ó–∞—è–≤–∫–∞ –ø–æ–¥–∞–Ω–∞" : "–ü–æ–¥–∞—Ç—å –∑–∞—è–≤–∫—É"}
                </button>
              </div>
            );
          })
        ) : (
          <div style={{ textAlign: "center", padding: "40px", color: "#94a3b8" }}>
            –ù–∏—á–µ–≥–æ –Ω–µ –Ω–∞–π–¥–µ–Ω–æ –ø–æ –≤–∞—à–µ–º—É –∑–∞–ø—Ä–æ—Å—É üòî
          </div>
        )}

      </div>
