import React, { createContext, useState, useContext, useEffect } from "react";

// 1. Создаем контекст (хранилище данных)
const AuthContext = createContext();

// 2. Провайдер - компонент, который будет "обнимать" всё наше приложение
export const AuthProvider = ({ children }) => {
  // Состояние: null = не вошел, object = данные пользователя
  const [user, setUser] = useState(null);

  // При первом запуске проверяем, не входили ли мы ранее (сохраним в localStorage)
  useEffect(() => {
    const savedUser = localStorage.getItem("user");
    if (savedUser) {
      setUser(JSON.parse(savedUser));
    }
  }, []);

  // Функция Входа (Login)
  const login = (userData) => {
    setUser(userData);
    localStorage.setItem("user", JSON.stringify(userData));
  };

  // Функция Выхода (Logout)
  const logout = () => {
    setUser(null);
    localStorage.removeItem("user");
  };

  return (
    <AuthContext.Provider value={{ user, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

// 3. Хук для удобного использования контекста в других файлах
export const useAuth = () => useContext(AuthContext);
