import React, { createContext, useState, useContext, useEffect } from "react";

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  
  // НОВОЕ: Список заявок пользователя
  const [userApplications, setUserApplications] = useState([
    // Стартовые фейковые данные (чтобы профиль не был пустым)
    { id: 101, company: "Сбербанк", position: "Стажер Java", status: "На рассмотрении", date: "24.11.2025" }
  ]);

  useEffect(() => {
    const savedUser = localStorage.getItem("user");
    if (savedUser) setUser(JSON.parse(savedUser));
  }, []);

  const login = (userData) => {
    setUser(userData);
    localStorage.setItem("user", JSON.stringify(userData));
  };

  const logout = () => {
    setUser(null);
    localStorage.removeItem("user");
  };

  // НОВОЕ: Функция подачи заявки
  const applyForPractice = (practice) => {
    const newApplication = {
      id: Date.now(), // Уникальный ID
      company: practice.company,
      position: practice.title,
      status: "На рассмотрении",
      date: new Date().toLocaleDateString()
    };
    setUserApplications([...userApplications, newApplication]);
  };

  return (
    <AuthContext.Provider value={{ user, login, logout, userApplications, applyForPractice }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
